import java.util.Scanner;

public class P1035new {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner cin = new Scanner(System.in);
		int i=0;
		while ( cin.hasNext() ){
			System.out.println(++i);
			System.out.println(cin.nextLine());
		}
		cin.close();
	}

}
